import Handlebars from "handlebars"
import { Component } from 'react'
import './App.css'

var oldHref = "http://localhost:3000"

document.addEventListener('DOMContentLoaded', function() {
  // Create reference to "Generate Email" button
  var cell = document.getElementById('undo');
  // Add event listener to the generateEmail variable, specifying 'click'
  cell?.addEventListener('click', function() {
    // var undo = document.getElementById('undo');
    cell!.style.color = "red";
  });
});

// const element = document.getElementById('newgame');
// element?.addEventListener('click', listenerFunction);

// function listenerFunction(this: HTMLElement, ev: Event) {
//   console.log("hello");
//   this.style.color = "red";  
// }

interface Cell {
  text: String;
  clazz: String;
  link: String;
  worker: String;
}

interface Cells {
  cells: Array<Cell>,
  template: HandlebarsTemplateDelegate<any>
}

interface Props {
}

class App extends Component<Props, Cells> {
  constructor(props: Props) {
    super(props);
    this.state = {
      cells: [
        { text: "", clazz: "playable", link: "/play?x=0&y=0&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=1&y=0&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=2&y=0&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=3&y=0&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=4&y=0&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=0&y=1&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=1&y=1&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=2&y=1&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=3&y=1&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=4&y=1&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=0&y=2&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=1&y=2&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=2&y=2&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=3&y=2&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=4&y=2&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=0&y=3&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=1&y=3&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=2&y=3&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=3&y=3&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=4&y=3&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=0&y=4&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=1&y=4&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=2&y=4&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=3&y=4&w=", worker: "" },
        { text: "", clazz: "playable", link: "/play?x=4&y=4&w=", worker: "" },
      ],
      template: this.loadTemplate()
    };
  }

  loadTemplate (): HandlebarsTemplateDelegate<any> {
    const src = document.getElementById("handlebars");
    return Handlebars.compile(src?.innerHTML, {});
  }

  convertToCell(p: any): Array<Cell> {
    const newCells: Array<Cell> = [];
    for (var i = 0; i < p["cells"].length; i++) {
      var c: Cell = {
        text: p["cells"][i]["text"],
        clazz: p["cells"][i]["clazz"],
        link: p["cells"][i]["link"],
        worker: p["cells"][i]["link"],

      };
      newCells.push(c);
    }

    return newCells;
  }



  async newGame() {
    const response = await fetch("newgame");
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    this.setState({ cells: newCells });
  }

  async play(url: String) {
    const href = "play?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    this.setState({ cells: newCells });
  }

  async switch() {
    if (
      window.location.href === "http://localhost:3000/newgame" &&
      oldHref !== window.location.href
    ) {
      this.newGame();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/play" &&
      oldHref !== window.location.href
    ) {
      this.play(window.location.href);
      oldHref = window.location.href;
    }
  };

  render() {
    this.switch();
    return (
      <div className="App">
        <div
          dangerouslySetInnerHTML={{
            __html: this.state.template({ cells: this.state.cells }),
          }}
        />
      </div>
    )
  };
};

export default App;
