import Handlebars from "handlebars"
import { Component } from 'react'
import './App.css'

var oldHref = "http://localhost:3000"

// document.addEventListener('DOMContentLoaded', function() {
//   // Create reference to "Generate Email" button
//   var cell = document.getElementById('cell');
//   const header = document.getElementById("instructions");
//   // Add event listener to the generateEmail variable, specifying 'click'
//   cell?.addEventListener('click', function() {
//     header!.textContent = "it will rain today";
//   });
// });

// var check = document.getElementById('cell');
// check = "";
// cell?.attributes('src') = "";

interface Cell {
  text: String;
  worker: String;
  link: String;
  image: String;
}

interface Cells {
  cells: Array<Cell>,
  template: HandlebarsTemplateDelegate<any>
}

interface Props {
}

class App extends Component<Props, Cells> {
  constructor(props: Props) {
    super(props);
    console.log(this);
    this.state = {
      cells: [
        { text: "", worker: "", link: "/play?x=0&y=0&w=", image: "" },
        { text: "", worker: "", link: "/play?x=1&y=0&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=2&y=0&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=3&y=0&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=4&y=0&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=0&y=1&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=1&y=1&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=2&y=1&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=3&y=1&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=4&y=1&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=0&y=2&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=1&y=2&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=2&y=2&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=3&y=2&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=4&y=2&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=0&y=3&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=1&y=3&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=2&y=3&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=3&y=3&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=4&y=3&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=0&y=4&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=1&y=4&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=2&y=4&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=3&y=4&w=", image: ""  },
        { text: "", worker: "", link: "/play?x=4&y=4&w=", image: ""  },
      ],
      template: this.loadTemplate()
    };
  }

// have cells be move or build with correct params
// those methods return the json obj of board
// update ui with convertToCell

  loadTemplate (): HandlebarsTemplateDelegate<any> {
    const src = document.getElementById("handlebars");
    return Handlebars.compile(src?.innerHTML, {});
  }

  convertToCell(p: any): Array<Cell> {
    // console.log(p);
    const newCells: Array<Cell> = [];
    for (var i = 0; i < p["cells"].length; i++) {
      for (var j = 0; j < p["cells"][i].length; j++) {
        var c: Cell = {
          text: p["cells"][i][j]["text"],
          worker: p["cells"][i][j]["worker"],
          link: p["cells"][i][j]["link"],
          image: p["cells"][i][j]["image"],
        };
        console.log(c);

        newCells.push(c);
      }
    }
    console.log(newCells);

    return newCells;
  }

  async newGame() {
    const response = await fetch("newgame");
    const json = await response.json();
    console.log(json);

    const newCells: Array<Cell> = this.convertToCell(json);
    this.setState({ cells: newCells });
  }

  async play(url: String) {
    const href = "play?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    const header = document.getElementById("instructions");
    header!.textContent = "it will rain today";

    this.setState({ cells: newCells });
  }

  async build(url: String) {
    const href = "build?" + url.split("?")[1];
    const response = await fetch(href);
    const json = await response.json();

    const newCells: Array<Cell> = this.convertToCell(json);
    this.setState({ cells: newCells });
  }

  async switch() {
    if (
      window.location.href === "http://localhost:3000/newgame" &&
      oldHref !== window.location.href
    ) {
      this.newGame();
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/play" &&
      oldHref !== window.location.href
    ) {
      this.play(window.location.href);
      oldHref = window.location.href;
    } else if (
      window.location.href.split("?")[0] === "http://localhost:3000/build" &&
      oldHref !== window.location.href
    ) {
      this.build(window.location.href);
      oldHref = window.location.href;
    }
  };

  render() {
    this.switch();
    return (
      <div className="App">
        <div
          dangerouslySetInnerHTML={{
            __html: this.state.template({ cells: this.state.cells }),
          }}
        />
      </div>
    )
  };
};

export default App;
